struct Solution;
impl Solution {
    pub fn num_subarray_product_less_than_k(nums: Vec<i32>, k: i32) -> i32 {
        let nums = nums.into_iter().filter(|i| *i < k).collect::<Vec<i32>>();
        
        if nums.is_empty() {
            return 0;
        }

        let all_product = nums.iter().map(|i| *i as i128).product::<i128>();
        if all_product < k as i128 {
            // 
            return 0;
        }

        let max_index = nums.len() - 1;
        let mut start = 0;
        let mut end = 0;

        // `1` is the last element
        let mut result_count = 1;

        while start != max_index || end != max_index {
            let product = nums[start..=end].iter().product::<i32>();

            if product < k {
                if end == max_index {
                    start += 1;
                    end = start;
                } else {
                    end += 1;
                }
                // println!("valid vec: {:?}", vec);
                result_count += 1;
            } else {
                start += 1;
                end = start;
            }
        }

        return result_count;
    }
}

